let solve markerLength (input : string) =
    let rec f (i : int) =
        if input[i .. i + markerLength - 1] |> Seq.distinct |> Seq.length = markerLength
            then i + markerLength
            else f (i + 1)
    f 0

let input = ""
input |> solve 4 |> printfn "Answer to Part One: %d"  // expected output is 1,766
input |> solve 14 |> printfn "Answer to Part Two: %d"  // expected output is 2,383